function date_change() {

	var date_input = document.querySelector(".date_temp");
	var date_split = date_input.innerText.split('/');

	var date_day = Math.floor(date_split[0]/10);

	if(date_day == 0) {
		date_split[0] = '0' + date_split[0];
    }

	var date = date_split[2].replace(/^\s+|\s+$/g,"") + date_split[0].replace(/^\s+|\s+$/g,"") + date_split[1].replace(/^\s+|\s+$/g,"");
	date_input.innerText = date;
	date_input.setAttribute("class", "date");


/*
	var date_input = $('.date_temp').text();
	var date_split = date_input.split('/');

