function scrollTo(element, dir) {

	if(dir == "up") {
		dir = element.offsetTop;
    } else {
		dir = element.offsetHeight;
    }
    window.scroll({
		behavior: 'smooth',
    	left: 0,
    	top: dir
	});
}


function on_key_down() {
	var keyCode = event.keyCode;

  if(keyCode == 81) {
	location.href = "https://www.blogger.com";

  }
}

